#include "libft.h"

void	*ft_memcpy(void *dst, const void *src, size_t size)
{
	unsigned char		*d;
	const unsigned char	*s;

	d = (unsigned char *) dst;
	s = (const unsigned char *) src;
	while (size)
	{
		*d++ = *s++;
		size--;
	}
	return (dst);
}
